  const link = document.createElement('a');
  link.href = href;
  link.className = className;
  link.textContent = text;
  return link;
}

linkData.forEach(linkObj => {
  const linkElement = linkMaker(linkObj);
  document.querySelector("nav").appendChild(linkElement);
});

